import React, { useState } from "react";
import Calendar from "react-calendar";
import 'react-calendar/dist/Calendar.css';
import "./FoodManage.css";

const FoodManage = () => {
  const [date, setDate] = useState(new Date());
  const [foods, setFoods] = useState([{ name: '', gram: '' }]);

  const handleAddRow = () => {
    setFoods([...foods, { name: '', gram: '' }]);
  };

  const handleInputChange = (index, field, value) => {
    const updatedFoods = [...foods];
    updatedFoods[index][field] = value;
    setFoods(updatedFoods);
  };

  const handleConfirm = () => {
    const formatted = foods
      .filter(f => f.name && f.gram)
      .map(f => `${f.name} ${f.gram}`)
      .join('\n');

    alert(`üìÖ ${date.toLocaleDateString()}:\n\nüìù GeminiÎ°ú Î≥¥ÎÇº ÎÇ¥Ïö©:\n${formatted}`);

    // Ïó¨Í∏∞Ïóê Gemini API Ìò∏Ï∂ú ÏΩîÎìú Ïó∞Í≤∞ Í∞ÄÎä•
  };

  return (
    <div className="food-manage-container">
      <h2 className="title">ÏãùÎã® Í¥ÄÎ¶¨</h2>

      <div className="calendar-wrapper">
        <Calendar onChange={setDate} value={date} />
      </div>

      <p className="selected-date">
        ÏÑ†ÌÉùÌïú ÎÇ†Ïßú: {date.toLocaleDateString()}
      </p>

      <div className="input-table">
        {foods.map((food, index) => (
          <div key={index} className="food-row">
            <input
              type="text"
              placeholder="ÏùåÏãùÎ™Ö"
              value={food.name}
              onChange={(e) => handleInputChange(index, "name", e.target.value)}
              className="food-input"
            />
            <input
              type="number"
              placeholder="Í∑∏Îû®Ïàò"
              value={food.gram}
              onChange={(e) => handleInputChange(index, "gram", e.target.value)}
              className="gram-input"
            />
          </div>
        ))}
        <button className="add-button" onClick={handleAddRow}>
          + ÏùåÏãù Ï∂îÍ∞Ä
        </button>
      </div>

      <button className="confirm-button" onClick={handleConfirm}>
        ÌôïÏù∏
      </button>
    </div>
  );
};

export default FoodManage;
