import React, { useState, useEffect } from "react";
import Calendar from "react-calendar";
import 'react-calendar/dist/Calendar.css';
import "./FoodManage.css";
import SendToGemini from "./SendToGemini"; // Í≤ΩÎ°ú Ï£ºÏùò: utilsÏóê ÎëêÎ©¥ './utils/sendToGemini'

const FoodManage = () => {
  const [date, setDate] = useState(new Date());
  const [inputText, setInputText] = useState("");
  const [geminiResult, setGeminiResult] = useState("");
  const [showInput, setShowInput] = useState(false);

  const handleDateChange = (newDate) => {
    setDate(newDate);
    setShowInput(true); // ÎÇ†Ïßú ÌÅ¥Î¶≠ Ïãú ÏûÖÎ†•Ï∞Ω Î≥¥Ïù¥Í≤å
    setInputText("");   // Ïù¥Ï†Ñ ÏûÖÎ†• Ï¥àÍ∏∞Ìôî
  };

  const handleConfirm = async () => {
    const formattedDate = date.toISOString().split("T")[0];

    // üîΩ 1. Gemini Ï†ÑÏÜ° (ÏÑ†ÌÉù)
    const geminiResponse = await SendToGemini(inputText);
    setGeminiResult(geminiResponse);

    // üîΩ 2. MySQL Ï†ÑÏÜ°
    try {
      const response = await fetch("http://localhost:8080/api/saveFoodLog", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          date: formattedDate,
          foodText: inputText,
          userId: "exampleUserId123" // Ïã§Ï†ú ÏÇ¨Ïö©Ïûê IDÏôÄ Ïó∞Í≤∞
        })
      });
      const result = await response.json();
      console.log("Ï†ÄÏû• ÏÑ±Í≥µ:", result);
    } catch (error) {
      console.error("Ï†ÄÏû• Ïã§Ìå®:", error);
    }
  };

  return (
    <div className="food-manage-container">
      <h2 className="title">üìÖ ÏãùÎã® Í∏∞Î°ù</h2>

      <Calendar onChange={handleDateChange} value={date} />

      <p className="selected-date">ÏÑ†ÌÉùÌïú ÎÇ†Ïßú: {date.toLocaleDateString()}</p>

      {showInput && (
        <div className="input-area">
          <textarea
            className="food-textarea"
            placeholder="Ïòà: Í≥†Íµ¨Îßà 200g, Îã≠Í∞ÄÏä¥ÏÇ¥ 150g"
            value={inputText}
            onChange={(e) => setInputText(e.target.value)}
            rows={4}
          />
          <button className="confirm-button" onClick={handleConfirm}>
            ‚úÖ ÌôïÏù∏ Î∞è Ï†ÄÏû•
          </button>
        </div>
      )}

      {geminiResult && (
        <div className="gemini-result">
          <h4>Gemini ÏùëÎãµ</h4>
          <pre>{geminiResult}</pre>
        </div>
      )}
    </div>
  );
};


export default FoodManage;
