import React, { useState } from "react";
import "./Memopage.css";

const initialPages = [
  "사물인터넷",
  "aiot",
  "소개 c",
  "web 프로젝트",
  "백엔드",
  "식단 관련 지식",
  "알고리즘 (구종만 책)",
  "알고리즘 개념",
  "다시 풀 것",
  "mysql문법",
  "이력서",
  "면접준비",
];

const Memopage = () => {
  const [selectedPage, setSelectedPage] = useState(initialPages[0]);
  const [pageContents, setPageContents] = useState({});

  const handleChange = (e) => {
    setPageContents({ ...pageContents, [selectedPage]: e.target.value });
  };

  return (
    <div className="memo-container">
      {/* Sidebar */}
      <div className="memo-sidebar">
        <h2 className="sidebar-title">개인 페이지</h2>
        <ul className="page-list">
          {initialPages.map((page) => (
            <li
              key={page}
              className={`page-item ${selectedPage === page ? "active" : ""}`}
              onClick={() => setSelectedPage(page)}
            >
              {page}
            </li>
          ))}
        </ul>
      </div>

      {/* Editor */}
      <div className="memo-editor">
        <h2 className="editor-title">{selectedPage}</h2>
        <textarea
          className="editor-textarea"
          placeholder="이 페이지에 메모를 작성하세요..."
          value={pageContents[selectedPage] || ""}
          onChange={handleChange}
        />
      </div>
    </div>
  );
};

export default Memopage;
