import React, { useEffect, useState } from "react";
import Calendar from "react-calendar";
import "react-calendar/dist/Calendar.css";
import "./RoutineManage.css";
import { auth, db } from "./firebase";
import { doc, getDoc, collection, getDocs } from "firebase/firestore";


const RoutineManage = () => {
  const [routineData, setRoutineData] = useState({});
  const [selectedPart, setSelectedPart] = useState(null);
  const [selectedWorkout, setSelectedWorkout] = useState(null);
  const [calendarDate, setCalendarDate] = useState(new Date());
  const [dailyRecord, setDailyRecord] = useState(null);
  const [routineDatesByPart, setRoutineDatesByPart] = useState({});

  useEffect(() => {
    const fetchRoutineDatesByPart = async () => {
      const user = auth.currentUser;
      if (!user) return;

      const snap = await getDocs(collection(db, "users", user.uid, "Calender", "health", "routines"));
      const grouped = {};

      snap.forEach((doc) => {
        const data = doc.data();
        const date = data["ÎÇ†Ïßú"];
        const part = data["Ïò§Îäò Ìïú Î£®Ìã¥Ïù¥Î¶Ñ"];

        if (!grouped[part]) grouped[part] = [];
        grouped[part].push(date);
      });

      setRoutineDatesByPart(grouped);
    };

    fetchRoutineDatesByPart();
  }, []);



  useEffect(() => {
    const fetchRoutine = async () => {
      const user = auth.currentUser;
      if (!user) return;

      const routineDoc = doc(db, "users", user.uid, "Routine", "Myroutine");
      const snapshot = await getDoc(routineDoc);
      if (snapshot.exists()) {
        setRoutineData(snapshot.data());
      }
    };

    fetchRoutine();
  }, []);

  useEffect(() => {
    const fetchDailyRoutine = async () => {
      const user = auth.currentUser;
      if (!user) return;

      const routinesCol = collection(db, "users", user.uid, "Calender", "health", "routines");
      const snap = await getDocs(routinesCol);

      const formatted = calendarDate.toISOString().split("T")[0]; // 2025-05-16 ÌòïÏãù
      let found = null;

      snap.forEach((doc) => {
        const data = doc.data();
        if (data.ÎÇ†Ïßú === formatted) {
          found = data;
        }
      });

      setDailyRecord(found);
    };

    fetchDailyRoutine();
  }, [calendarDate]);

  return (
    <div className="routine-container">
      <h2 className="routine-title">üî• ÎÇòÏùò Ïö¥Îèô Î£®Ìã¥</h2>
      <div className="routine-section">
        {Object.entries(routineData).map(([part, workouts]) => (
          <div key={part} className="routine-part">
            <h3
            className={`routine-part-title ${selectedPart === part ? "selected-part" : ""}`}
            onClick={() => setSelectedPart(part === selectedPart ? null : part)}
            >
            - {part}
            </h3>
            {selectedPart === part &&
              workouts.map((workout, index) => {
                const workoutName = Object.keys(workout)[0];
                const exercises = workout[workoutName].exercises;

                return (
                  <div key={index} className="routine-workout">
                    <p
                      className="routine-workout-name"
                      onClick={() =>
                        setSelectedWorkout(selectedWorkout === index ? null : index)
                      }
                    >
                      - {workoutName}
                    </p>
                    {selectedWorkout === index && (
                      <div className="routine-sets">
                        {exercises.map((ex, i) => (
                          <div key={i} className="routine-set-item">
                            ÏÑ∏Ìä∏ {i + 1}: {ex.reps}Ìöå √ó {ex.weight}kg
                          </div>
                        ))}
                      </div>
                    )}
                  </div>
                );
              })}
          </div>
        ))}
      </div>

      <div className="calendar-box">
        <h3 className="calendar-title">üìÖ Î£®Ìã¥ Í∏∞Î°ù Îã¨Î†•</h3>
        <Calendar
            onChange={setCalendarDate}
            value={calendarDate}
            className="custom-calendar"
            tileClassName={({ date }) => {
                const formatted = date.toISOString().split("T")[0];
                const matchedDates = routineDatesByPart[selectedPart] || [];

                return matchedDates.includes(formatted) ? "highlighted-day" : null;
            }}
        />
        <p className="calendar-selected-date">
          ÏÑ†ÌÉùÎêú ÎÇ†Ïßú: {calendarDate.toLocaleDateString()}
        </p>

        {dailyRecord ? (
  <div className="calendar-record">
    <h4>üìù Ïö¥Îèô Í∏∞Î°ù</h4>
    <p>Î£®Ìã¥ Ïù¥Î¶Ñ: {dailyRecord["Ïò§Îäò Ìïú Î£®Ìã¥Ïù¥Î¶Ñ"]}</p>
    <p>Ï¥ù ÏÑ∏Ìä∏Ïàò: {dailyRecord["Ïò§Îäò Ï¥ù ÏÑ∏Ìä∏Ïàò"]}</p>
    <p>Ï¥ù Î≥ºÎ•®: {dailyRecord["Ïò§Îäò Ï¥ù Î≥ºÎ•®"]}</p>
    <p>ÏãúÏûë ÏãúÍ∞Ñ: {dailyRecord["Ïö¥Îèô ÏãúÏûë ÏãúÍ∞Ñ"]}</p>
    <p>Ï¢ÖÎ£å ÏãúÍ∞Ñ: {dailyRecord["Ïö¥Îèô Ï¢ÖÎ£å ÏãúÍ∞Ñ"]}</p>

    <h5 style={{ marginTop: "1rem" }}>üìã Ïö¥Îèô Î™©Î°ù</h5>
    {Array.isArray(dailyRecord["Ïö¥Îèô Î™©Î°ù"]) ? (
      dailyRecord["Ïö¥Îèô Î™©Î°ù"].map((exercise, idx) => (
        <div key={idx} style={{ marginBottom: "1rem" }}>
          <strong>- {exercise["Ïö¥Îèô Ïù¥Î¶Ñ"]}</strong>
          {Array.isArray(exercise["ÏÑ∏Ìä∏"]) ? (
            <ul style={{ marginLeft: "1rem" }}>
              {exercise["ÏÑ∏Ìä∏"].map((set, i) => (
                <li key={i}>
                  ÏÑ∏Ìä∏ {i + 1}: {set.reps}Ìöå √ó {set.weight}kg
                </li>
              ))}
            </ul>
          ) : (
            <p style={{ marginLeft: "1rem" }}>ÏÑ∏Ìä∏ Ï†ïÎ≥¥ ÏóÜÏùå</p>
          )}
        </div>
      ))
    ) : (
      <p>Ïö¥Îèô Í∏∞Î°ùÏù¥ ÏóÜÏäµÎãàÎã§.</p>
    )}
  </div>
) : (
  <p className="calendar-no-record">Ïù¥ ÎÇ†ÏóêÎäî Í∏∞Î°ùÏù¥ ÏóÜÏäµÎãàÎã§.</p>
)}

      </div>
    </div>
  );
};

export default RoutineManage;
