import React, { useEffect, useState } from "react";
import { auth, db } from "./firebase";
import { doc, getDoc } from "firebase/firestore";

const RoutineManage = () => {
  const [routineData, setRoutineData] = useState({});
  const [selectedPart, setSelectedPart] = useState(null);
  const [selectedWorkout, setSelectedWorkout] = useState(null);

  useEffect(() => {
    const fetchRoutine = async () => {
      const user = auth.currentUser;
      if (!user) return;

      const routineDoc = doc(db, "users", user.uid, "Routine", "Myroutine");
      const snapshot = await getDoc(routineDoc);
      if (snapshot.exists()) {
        setRoutineData(snapshot.data());
      }
    };

    fetchRoutine();
  }, []);

  return (
    <div style={{ padding: "20px" }}>
      <h2>🔥 나의 운동 루틴</h2>
      <div>
        {Object.entries(routineData).map(([part, workouts]) => (
          <div key={part}>
            <h3 onClick={() => setSelectedPart(part)} style={{ cursor: "pointer" }}>
              {part}
            </h3>
            {selectedPart === part &&
              workouts.map((workout, index) => (
                <div key={index}>
                  <p
                    style={{ marginLeft: "20px", cursor: "pointer", fontWeight: "bold" }}
                    onClick={() => setSelectedWorkout(workout)}
                  >
                    {Object.keys(workout)[0]} {/* 운동명 */}
                  </p>
                  {selectedWorkout === workout &&
                    workout[Object.keys(workout)[0]].exercises.map((ex, i) => (
                      <div key={i} style={{ marginLeft: "40px" }}>
                        ➤ 세트 {i + 1}: {ex.reps}회 x {ex.weight}kg
                      </div>
                    ))}
                </div>
              ))}
          </div>
        ))}
      </div>
    </div>
  );
};

export default RoutineManage;
